
var utility = require('utility');

var EpsilonClosure = function () {
    this._set = new Set();      //contains State
    this._owners = new Set();   //contains State
};
EpsilonClosure.prototype.addState = function (state) {
    this._set.add(state);
    return this;
};
EpsilonClosure.prototype.addOwner = function (state) {
    this._owners.add(state);
    return this;
};
EpsilonClosure.prototype.removeOwner = function (state) {
    this._owners.delete(state);
    return this;
};
EpsilonClosure.prototype.getOwners = function (owner) {
    return utility.genToArr(this._owners.values());
};
EpsilonClosure.prototype.getStates = function () {
    return utility.genToArr(this._set.values());
};

module.exports = EpsilonClosure;
