
var isStr = function (input) {
    return Object.prototype.toString.call(input) == '[object String]';
};

var genToArr = function (gen) {
    var ret = [];
    var n = gen.next();
    while(!n.done) {
        ret.push(n.value);
        n = gen.next();
    }
    return ret;
};

exports.isStr = isStr;
exports.genToArr = genToArr;
