
import LR1Parser from './lr1parser';
import SLR1Parser from './slr1parser';
import * as prod from 'productions';

export function createSLR1Parser(mprodarr: Iterable<string>): SLR1Parser {
    return new SLR1Parser(prod.createProdSet(mprodarr));
}

export function createLR1Parser(mprodarr: Iterable<string>): LR1Parser {
    return new LR1Parser(prod.createProdSet(mprodarr));
}
