
import * as p from 'productions';

export interface Item {
    prod: p.Production;
    dot: number;
}

export function createItems(prod: p.Production) {
    let i = 0, rhs = prod.getRHS();
    let len = rhs.length, ret = new Array<Item>();
    while (i <= len) {
        ret.push({
            prod: prod,
            dot: i
        });
        ++i;
    }
    return ret;
}