
var utility = require('utility');

var Transition = function (srcNode, tgtNode, symbol) {
    if (!utility.isNonNegInt(srcNode) || !utility.isNonNegInt(tgtNode) || !utility.isStr(symbol)) throw new Error(['bad input for transition:', srcNode, tgtNode, symbol].join(' '))
    this.srcNode = srcNode;
    this.tgtNode = tgtNode;
    this.symbol = symbol;
};
Transition.prototype.print = function () {
    console.log([this.srcNode, ' --', this.symbol, '-> ', this.tgtNode].join(''));
};

exports.Transition = Transition;
