
var assert = require('assert');
var nfa = require('nfa');
var dfa = require('dfa');
var automata = require('automata');

var Transition = automata.Transition;

var tran = function (srcNode, tgtNode, symbol) {
    return new Transition(srcNode, tgtNode, symbol);
};

describe('nfa convert to dfa', function () {
    it('epsilon 1', function () {
        var nfA = nfa.createNFA([
            tran(0, 0, 'a'),
            tran(0, 1, ''),
            tran(1, 1, 'b'),
            tran(1, 2, ''),
            tran(2, 2, 'c'),
            tran(2, 3, ''),
            tran(3, 3, 'd')
        ], [0], [3]);
        var dfA = nfA.toDFA();
        var dfA2 = dfa.createDFA([
            tran(10, 10, 'a'),
            tran(10, 11, 'b'),
            tran(10, 12, 'c'),
            tran(10, 13, 'd'),
            tran(11, 11, 'b'),
            tran(11, 12, 'c'),
            tran(11, 13, 'd'),
            tran(12, 12, 'c'),
            tran(12, 13, 'd'),
            tran(13, 13, 'd')
        ], 10, [10, 11, 12, 13]);
        assert.equal(true, dfA2.equivalent(dfA));
    });
});
