
var prod = require('productions');
var utility = require('utility');
var assert = require('assert');

var validate = function (pset, expected) {
    var allLHS = pset.getAllNonTerminals();
    assert.equal(true, utility.arrayEquivalent(allLHS, expected.map(function (e) { return e.lhs; })));
    for (var i = 0; i < expected.length; ++i) {
        var item = expected[i];
        assert.equal(true, utility.arrayEquivalent(item.rhsArr, pset.getRHSArr(item.lhs), function (test, real) {
            //test and real are both array
            if (test.length !== real.length) return false;
            for (var q = 0; q < test.length; ++q) {
                var titem = test[q], symbol = real[q];
                if (titem.str !== symbol.getName() || titem.terminal !== symbol.isTerminal()) return false;
            }
            return true;
        }));
    }
};


describe('production set left factoring', function () {
    it('simple 1', function () {
        var pset = prod.createProdSet([
            'E -> T + E | T',
            'T -> int | int * T | ( E )'
        ]);
        var lfprodset = pset.leftFactoredProdSet();
        var new1 = lfprodset.getRHSArr('E')[0][1].getName();
        var trhsarr = lfprodset.getRHSArr('T');
        var new2 = trhsarr[trhsarr[0].length == 2 ? 0 : 1][1].getName();
        validate(lfprodset, [
            {
                lhs: 'E',
                rhsArr: [
                    [
                        { str: 'T', terminal: false },
                        { str: new1, terminal: false }
                    ]
                ]
            }, {
                lhs: new1,
                rhsArr: [
                    [
                        { str: '+', terminal: true },
                        { str: 'E', terminal: false }
                    ],
                    []
                ]
            }, {
                lhs: 'T',
                rhsArr: [
                    [
                        { str: 'int', terminal: true },
                        { str: new2, terminal: false }
                    ],
                    [
                        { str: '(', terminal: true },
                        { str: 'E', terminal: false },
                        { str: ')', terminal: true }
                    ]
                ]
            }, {
                lhs: new2,
                rhsArr: [
                    [],
                    [
                        { str: '*', terminal: true },
                        { str: 'T', terminal: false }
                    ]
                ]
            }
        ]);
    });

    it('simple 2: no need left factoring', function () {
        var pset = prod.createProdSet([
            'E -> int + int | '
        ]);
        validate(pset.leftFactoredProdSet(), [
            {
                lhs: 'E',
                rhsArr: [
                    [
                        { str: 'int', terminal: true },
                        { str: '+', terminal: true },
                        { str: 'int', terminal: true }
                    ],
                    []
                ]
            }
        ]);
    });

    it('simple 3: 2 left factor for one non-terminal', function () {
        var pset = prod.createProdSet([
            'E -> T + E | T | Q | Q * m | w',
            'T -> int | int * T | ( E )',
            'Q -> u'
        ]);
        var lfprodset = pset.leftFactoredProdSet();
        var Erhsarr = lfprodset.getRHSArr('E');
        var new1 = null, new2 = null, new3 = null;
        for (var i = 0; i < 3; ++i) {
            var rhs = Erhsarr[i];
            if (rhs.length === 2) {
                if (rhs[0].getName() === 'T') new1 = rhs[1].getName();
                else new2 = rhs[1].getName();
            }
        }
        var Trhsarr = lfprodset.getRHSArr('T');
        new3 = Trhsarr[Trhsarr[0].length == 2 ? 0 : 1][1].getName();
        validate(lfprodset, [
            {
                lhs: 'E',
                rhsArr: [
                    [
                        { str: 'T', terminal: false },
                        { str: new1, terminal: false }
                    ],
                    [
                        { str: 'Q', terminal: false },
                        { str: new2, terminal: false }
                    ],
                    [
                        { str: 'w', terminal: true }
                    ]
                ]
            }, {
                lhs: new1,
                rhsArr: [
                    [
                        { str: '+', terminal: true },
                        { str: 'E', terminal: false }
                    ],
                    []
                ]
            }, {
                lhs: new2,
                rhsArr: [
                    [
                        { str: '*', terminal: true },
                        { str: 'm', terminal: true }
                    ],
                    []
                ]
            }, {
                lhs: 'T',
                rhsArr: [
                    [
                        { str: 'int', terminal: true },
                        { str: new3, terminal: false }
                    ],
                    [
                        { str: '(', terminal: true },
                        { str: 'E', terminal: false },
                        { str: ')', terminal: true }
                    ]
                ]
            }, {
                lhs: new3,
                rhsArr: [
                    [],
                    [
                        { str: '*', terminal: true },
                        { str: 'T', terminal: false }
                    ]
                ]
            }, {
                lhs: 'Q',
                rhsArr: [
                    [
                        { str: 'u', terminal: true }
                    ]
                ]
            }
        ]);
    });


    it('simple 4: 2 layer of left factoring', function () {
        var pset = prod.createProdSet([
            'E -> int + q | int + m | int +'
        ]);
        validate(pset.leftFactoredProdSet(), [
            {
                lhs: 'E',
                rhsArr: [
                    [
                        { str: 'int', terminal: true },
                        { str: '+', terminal: true },
                        { str: 'D__0', terminal: false }
                    ]
                ]
            }, {
                lhs: 'D__0',
                rhsArr: [
                    [
                        { str: 'q', terminal: true }
                    ],
                    [
                        { str: 'm', terminal: true }
                    ],
                    []
                ]
            }
        ]);
    });


    it('simple 4: 2 layer of left factoring-2', function () {
        var pset = prod.createProdSet([
            'E -> int + q | int + m | int * w'
        ]);
        validate(pset.leftFactoredProdSet(), [
            {
                lhs: 'E',
                rhsArr: [
                    [
                        { str: 'int', terminal: true },
                        { str: 'D__0', terminal: false }
                    ]
                ]
            }, {
                lhs: 'D__0',
                rhsArr: [
                    [
                        { str: '+', terminal: true },
                        { str: 'D__1', terminal: false }
                    ],
                    [
                        { str: '*', terminal: true },
                        { str: 'w', terminal: true }
                    ]
                ]
            }, {
                lhs: 'D__1',
                rhsArr: [
                    [
                        { str: 'q', terminal: true }
                    ],
                    [
                        { str: 'm', terminal: true }
                    ]
                ]
            }
        ]);
    });


    it('simple 4: 2 layer of left factoring-3', function () {
        var pset = prod.createProdSet([
            'E -> int + q | int + m | int'
        ]);
        validate(pset.leftFactoredProdSet(), [
            {
                lhs: 'E',
                rhsArr: [
                    [
                        { str: 'int', terminal: true },
                        { str: 'D__0', terminal: false }
                    ]
                ]
            }, {
                lhs: 'D__0',
                rhsArr: [
                    [
                        { str: '+', terminal: true },
                        { str: 'D__1', terminal: false }
                    ],
                    []
                ]
            }, {
                lhs: 'D__1',
                rhsArr: [
                    [
                        { str: 'q', terminal: true }
                    ],
                    [
                        { str: 'm', terminal: true }
                    ]
                ]
            }
        ]);
    });
});
