
import * as utility from './index';

export class Transition implements utility.Edge {
    src: number;
    tgt: number;
    str: string;
    print(): this {
        console.log(this.src + '-' + this.str + '->' + this.tgt);
        return this;
    }
    constructor(src: number, tgt: number, str: string) {
        this.src = src;
        this.tgt = tgt;
        this.str = str;
    }
}